select o.order_type, o.order_no, s.supplier_name, o.employee_name, o.order_date, o.invalid, o.status,od.material_name, od.material_code,od.batch_no, od.style, od.storage_name, od.unit_name, od.order_count,od.order_flag, o.verify_person, o.verify_date from orders o, order_detail od, supplier s
o.order_type = 2 and o.order_no = od.order_no and o.supplier_id = s.supplier_id
limit #{param1},#{param2}

select o.order_type, o.order_no, s.supplier_name, o.employee_name, o.order_date,
o.invalid, o.status,od.material_name, od.material_code,od.batch_no,
od.style, od.storage_name, od.unit_name, od.order_count,od.order_flag,
o.verify_person, o.verify_date from orders o, order_detail od, supplier s

    o.order_type = 1 and o.order_no = od.order_no and o.supplier_id = s.supplier_id

        and o.supplier_Id = #{param3.supplierId}


        and o.invalid = #{param3.invalid}


        and o.status = #{param3.status}


        and o.employee_name = #{param3.employeeName}


        and o.order_date between #{param3.orderDate1} and #{param3.orderDate2}


limit #{param1},#{param2}

select count(*) from orders o, order_detail od, supplier s

    o.order_no = od.order_no and o.supplier_id = s.supplier_id

        and o.supplier_Id = #{param3.supplierId}


        and o.invalid = #{param3.invalid}


        and o.status = #{param3.status}


        and o.employee_name = like concat('%',#{employeeName},'%')


        and o.order_date between #{orderDate1} and #{orderDate2}

